import React from 'react';
import { Link } from 'react-router-dom';

const Films = props => (
    <div className="container">
        <div className="row">
            {props.films.map((film) => {
                return (
                    <div className="col-md-4" style={{ marginBottom: "1rem", marginTop: "2rem" }}>
                        <div className="recipes__box">
                            <img
                                className="recipe__box-img"
                                src={film.Poster}
                                alt={film.Title} />
                            <div className="recipe__text">
                                <h5 className="recipes__title">{film.Title.length < 20 ? `${film.Title}` : `${film.Title.substring(0, 25)}...`}</h5>
                                <p className="recipes__subtitle">Type : <span>{film.Type}</span></p>
                                <button className="recipe_buttons">
                                    <Link to={{ pathname: `/film/${film.imdbId}` }}>Lihat Film</Link>
                                </button>

                            </div>
                        </div>
                    </div>
                );
            })}

        </div>

    </div>
);

export default Films;
