import React from 'react';
import { Link } from 'react-router-dom';





const API_KEY = "27f4ba3a";

class Film extends React.Component {
    state = {
        filmPilih: []
    }

    componentDidMount = async () => {
        const title = this.props.location.state.film;
        const req = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&s=${title}"`);
        const res = await req.json();
        this.setState({ filmPilih: res.Search[0] })
        console.log(this.state.filmPilih);

    }
    render() {
        console.log(this.props);
        const film = this.state.filmPilih;
        return (

            <div className="container">
                {this.state.filmPilih.length !== 0 &&
                    <div className="active-recipe">
                        <img className="active-recipe__img" src={film.Poster} alt={film.Title}></img>
                        <h3 className="active-recipe__title">{film.Title}</h3>
                        <h4 className="active-recipe__publisher">Type : <span>
                            {film.Type}</span>
                        </h4>
                        <p className="active-recipe__publisher">Tahun : <span>{film.Year}</span></p>
                        <button className="active-recipe__button">
                            <Link to="/">Go Home</Link>
                        </button>
                    </div>
                }
            </div>
        );
    }
}

export default Film;
