import React, { Component } from 'react';
import './App.css';
import Form from "./components/Form";
const API_KEY = "27f4ba3a";

class App extends Component {

  state = {
    films: []
  }
  getFilm = async (e) => {
    const filmName = e.target.elements.filmName.value;
    e.preventDefault();
    const api_call = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&s=avengers"`);

    const data = await api_call.json();
    this.setState({ films: data });
    console.log(this.state.films);
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">Film Search</h1>
        </header>
        <Form getFilm={this.getFilm} />
      </div>
    );
  }
}

export default App;
